/*Table structure for table `seaking_instancedungeon` */

DROP TABLE IF EXISTS `seaking_instancedungeon`;

CREATE TABLE `seaking_instancedungeon` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `induId` varchar(60) NOT NULL DEFAULT '' COMMENT '副本Id',
  `name` varchar(100) NOT NULL DEFAULT '' COMMENT '副本名称',
  `cityId` int(10) NOT NULL DEFAULT '0' COMMENT '城市Id',
  `type` int(1) NOT NULL DEFAULT '1' COMMENT '1 - 任务副本 2 - boss副本 3 - 世界副本 4 - 神秘副本 5 - 材料副本',
  `maxIndex` tinyint(4) NOT NULL DEFAULT '0' COMMENT '副本位置Id',
  `induData` text NOT NULL COMMENT '怪物数据和宝箱数据[{"mgid":1,"eventId":1,"showMonsterId":1}]',
  `enterCondition` varchar(100) NOT NULL DEFAULT '' COMMENT 'enterCondition',
  `enterConditionValue` varchar(100) NOT NULL DEFAULT '' COMMENT 'enterConditionValue',
  `resourcePath` varchar(100) NOT NULL DEFAULT '' COMMENT 'resourcePath',
  `getExp` int(10) NOT NULL DEFAULT '0' COMMENT 'getExp',
  `getMoney` int(10) NOT NULL DEFAULT '0' COMMENT 'getMoney',
  `getItems` varchar(100) NOT NULL DEFAULT '' COMMENT 'getItems',
  `date` int(10) NOT NULL DEFAULT '0' COMMENT '日期',
  `bz` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1 - 可用 2 - 不可用',
  `updateBz` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1 - 新增 2 - 已更新到mysql',
  PRIMARY KEY (`id`),
  KEY `index_bz` (`bz`),
  KEY `index_updateBz` (`updateBz`)
) ENGINE=InnoDB AUTO_INCREMENT=88 DEFAULT CHARSET=utf8 COMMENT='副本表';

