/* Procedure structure for procedure `load_character_nicknames` */

/*!50003 DROP PROCEDURE IF EXISTS  `load_character_nicknames` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `load_character_nicknames`()
begin
declare v_max int unsigned default 1000;
declare v_counter int unsigned default 0;
DECLARE nickname varchar(60);
  TRUNCATE TABLE character_nicknames;
  START TRANSACTION;
  WHILE v_counter < v_max DO
    set nickname = CONCAT('昵称',v_counter);
    INSERT INTO character_nicknames (NAME) VALUES (nickname);
    SET v_counter=v_counter+1;
  END WHILE;
  COMMIT;
end */$$
DELIMITER ;

